import React, { useState } from 'react';


const App = () => {

const purple ="#8e44ad";
const [bg, setBg] = useState(purple);
const [name, setName] = useState("Click Me")

const bgChange = () => {
 
    let newBg ="#34495e";
    setBg(newBg);
    setName("Ouch!! ");
}

return(

    <>
    <div>
    <button onClick={bgChange}> Click Me </button>
    </div>
    </>
);
};

export default App;
